// =============================================================================
// Example Usage in Vue config
// =============================================================================

const LessFrameworkWebpack = require('./src/_library/less/core/node_modules/webpack-plugin');
const lessFrameworkWebpack = new LessFrameworkWebpack.plugin({
    baseDir: __dirname,
    pathCore: "src/_library/less/core",
    pathSite: "src/_library/less/site",
    pathJavascript: "src/_library/js/less-includes.js",
    consoleLogs: true,
    consoleLogsDev: false,
    formatLogs: true,
    formatLogsSimplePaths: true
  });

module.exports = {
  configureWebpack: {
    plugins: [
      lessFrameworkWebpack
    ]
  },
  css: {
    loaderOptions: {
      less: lessFrameworkWebpack.lessLoaderOptions(),
    }
  }
}